script:
# Auto set employee for Employee role users
if frappe.session.user != 'Administrator':
    # Check if user has Employee role
    user_roles = frappe.get_roles()
    if 'Employee' in user_roles:
        # Get employee linked to this user
        employee = frappe.db.get_value('Employee', {'user_id': frappe.session.user}, 'name')
        if employee:
            doc.employee = employee
        else:
            frappe.throw('No employee record found for user {0}'.format(frappe.session.user))

# Update total hours from time logs
total_hours = 0
if doc.time_logs:
    for time_log in doc.time_logs:
        if time_log.taken_hours:
            total_hours += time_log.taken_hours

doc.total_hours = round(total_hours, 2)

# Set default status for Employee role users
if not doc.status:
    user_roles = frappe.get_roles()
    if 'Employee' in user_roles:
        doc.status = 'Draft'
